
public class For02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
// 1 ~ 10까지 합구하기
		
		int sum=0;
		for(int i=1; i<=10; i++){
			sum = sum + i;   // sum += i;
//			1   =  0  + 1	
//			3   =  1  + 2	
//			6	=  3  + 3		
//			System.out.println("1~"+i+"합은="+sum);	
		}
		System.out.println("1~10 합은="+sum);		
	}

}



