
public class Oper01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// 산술 연산자(+, -, *, /, %)

		int a = 10, b = 4, c;

		c = a + b; // c=14
		System.out.println(a + "+" + b + "=" + c);

		c = a - b; // c=6
		System.out.println(a + "-" + b + "=" + c);

		c = a * b; // c=40
		System.out.println(a + "*" + b + "=" + c);

		c = a / b; // c=2 
		System.out.println(a + "/" + b + "=" + c);

		c = a % b; // c=2 (나머지 값)
		System.out.println(a + "%" + b + "=" + c);

	}

}
