
public class Oper02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String a = "Apple";
		String b = "Banana";
		String c = a + b;
		System.out.println("a+b=" + c);

		String str = "결과값";
		int n = 10;
		System.out.println("str+n=" + str + n);
		System.out.println("결과값:" + n);

	}

}
